function myStart(){document.getElementById("big").addEventListener("click",increase),document.getElementById("small").addEventListener("click",decrease)}function increase(){var e=document.getElementById("body"),t=window.getComputedStyle(e,null).getPropertyValue("font-size"),n=parseFloat(t);e.style.fontSize=n+1+"px"}function decrease(){var e=document.getElementById("body"),t=window.getComputedStyle(e,null).getPropertyValue("font-size"),n=parseFloat(t);e.style.fontSize=n-1+"px"}document.addEventListener("DOMContentLoaded",myStart);